
const inputDate = document.querySelectorAll('.form__field--date');

if( inputDate ) {
  inputDate.forEach(function(input) {

    input.addEventListener('focus', function() {

      this.type='date';
      this.showPicker();

    });

    input.addEventListener('blur', function() {

      this.type='text';

    });

  });
}


