
const inputDate = document.querySelectorAll('.form__field--date');

if( inputDate ) {
  inputDate.forEach(function(input) {

    input.addEventListener('focus', function() {

      this.type='date';
      this.showPicker();

    });

    input.addEventListener('blur', function() {

      this.type='text';

    });

  });
}


/* =============================== Sliders =============================== */

const introSlider = new Swiper('.intro__slider', {
    navigation: {
      nextEl: '.intro__slider-button--next',
      prevEl: '.intro__slider-button--prev',
    },
    effect: 'fade',
    crossFade: true,
    speed: 900,
    slidesPerView: 1,
    spaceBetween: 0,
    speed: 1000,
});